expectedSolution.js:
document.getElementById('emailForm').addEventListener('submit', function(e) {
  e.preventDefault();
  const email = document.getElementById('email').value;
  const message = document.getElementById('message');

  if (email.trim() === '') {
    message.textContent = 'Please enter an email';
    message.className = 'error';
  } else {
    message.textContent = 'Form submitted!';
    message.className = 'success';
  }
});

expectedSolution.css:
body {
  margin: 0;
  font-family: Arial, sans-serif;
}

.navbar {
  position: fixed;
  top: 0;
  width: 100%;
  background-color: #1f2937;
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 1000;
}

.nav-list {
  list-style: none;
  display: flex;
  margin: 0;
  padding: 0;
  gap: 40px;
}

.nav-item a, .dropbtn {
  color: white;
  text-decoration: none;
  font-weight: bold;
  cursor: pointer;
  padding: 5px 10px;
  display: block;
}

.nav-item a:hover, .dropbtn:hover {
  color: #60a5fa;
}

.dropdown {
  position: relative;
}

.dropdown-content {
  display: none;
  position: absolute;
  top: 100%;
  left: 0;
  background-color: #1f2937;
  min-width: 160px;
  box-shadow: 0 8px 16px rgba(0,0,0,0.3);
  border-radius: 5px;
  z-index: 1001;
}

.dropdown-content li a {
  padding: 10px;
  color: white;
}

.dropdown-content li a:hover {
  background-color: #2563eb;
  color: white;
}

.dropdown:hover .dropdown-content {
  display: block;
}

/* Hamburger button - hidden on desktop */
.hamburger {
  display: none;
  font-size: 28px;
  background: none;
  border: none;
  color: white;
  cursor: pointer;
}

/* Responsive styles */
@media (max-width: 768px) {
  .nav-list {
    flex-direction: column;
    display: none;
    width: 100%;
    background-color: #1f2937;
    position: absolute;
    top: 60px;
    left: 0;
    border-radius: 0 0 10px 10px;
  }

  .nav-list.active {
    display: flex;
  }

  .dropdown-content {
    position: static;
    box-shadow: none;
  }

  .dropdown:hover .dropdown-content {
    display: none;
  }

  .dropdown .dropdown-content.active {
    display: flex;
    flex-direction: column;
  }

  .hamburger {
    display: block;
  }
}

expectedSolution.js:
const hamburger = document.querySelector('.hamburger');
const navList = document.querySelector('.nav-list');
const dropdown = document.querySelector('.dropdown');
const dropbtn = document.querySelector('.dropbtn');
const dropdownContent = document.querySelector('.dropdown-content');

hamburger.addEventListener('click', () => {
  const expanded = hamburger.getAttribute('aria-expanded') === 'true';
  hamburger.setAttribute('aria-expanded', !expanded);
  navList.classList.toggle('active');
});

dropbtn.addEventListener('click', (e) => {
  if (window.innerWidth <= 768) {
    e.preventDefault();
    const expanded = dropbtn.getAttribute('aria-expanded') === 'true';
    dropbtn.setAttribute('aria-expanded', !expanded);
    dropdownContent.classList.toggle('active');
  }
});
